import React, { useState } from "react";
import axios from "axios";
import "./styles.css";

const API_URL = "http://localhost:8000/api/zamowienia";

function OrderItemsList() {
  const [order, setOrder] = useState({
    klient_id: "",
    produkt_id: "",
    ilosc: "",
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setOrder(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post(API_URL, order)
      .then(() => {
        setMessage("Zamówienie zostało zapisane!");
        setOrder({ klient_id: "", produkt_id: "", ilosc: "" });
      })
      .catch(err => {
        console.error("Błąd składania zamówienia:", err);
        setMessage("Wystąpił błąd podczas składania zamówienia.");
      });
  };

  return (
    <div className="container">
      <h2>Nowe Zamówienie</h2>
      <form onSubmit={handleSubmit} className="order-form">
        <div className="form-group">
          <label>Klient ID:</label>
          <input
            type="number"
            name="klient_id"
            value={order.klient_id}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Produkt ID:</label>
          <input
            type="number"
            name="produkt_id"
            value={order.produkt_id}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Ilość:</label>
          <input
            type="number"
            name="ilosc"
            value={order.ilosc}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn">Złóż zamówienie</button>
      </form>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default OrderItemsList;
