import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";  // Twój plik stylów - zawiera hover, kolory itd.

const API_URL = "http://localhost:8000/api/products"; // Dopasuj jeśli masz inny backend URL

function ProductList() {
  const [products, setProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [editedProduct, setEditedProduct] = useState({
    nazwa: "",
    parametr: "",
    cena_brutto: "",
  });

  // Pobranie produktów z API
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get(API_URL)
      .then(res => setProducts(res.data))
      .catch(err => console.error("Błąd pobierania danych:", err));
  };

  // Przejście w tryb edycji
  const handleEdit = (product) => {
    setEditingProductId(product.produkt_id);
    setEditedProduct({
      nazwa: product.nazwa,
      parametr: product.parametr,
      cena_brutto: product.cena_brutto,
    });
  };

  // Anulowanie edycji
  const handleCancel = () => {
    setEditingProductId(null);
  };

  // Obsługa zmiany pól edycji
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct(prev => ({ ...prev, [name]: value }));
  };

  // Zapis zmian do API
  const handleSave = () => {
    axios.put(`${API_URL}/${editingProductId}`, editedProduct)
      .then(() => {
        fetchProducts();  // odśwież dane
        setEditingProductId(null);
      })
      .catch(err => console.error("Błąd zapisu:", err));
  };

  // Usuwanie produktu
  const handleDelete = (id) => {
    if (window.confirm("Czy na pewno chcesz usunąć produkt?")) {
      axios.delete(`${API_URL}/${id}`)
        .then(() => fetchProducts())
        .catch(err => console.error("Błąd usuwania:", err));
    }
  };

  return (
    <div className="container">
      <h2>Lista Produktów</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nazwa</th>
            <th>Opis</th>
            <th>Cena</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, idx) => (
            <tr key={product.produkt_id} className={idx % 2 === 0 ? "even-row" : "odd-row"}>
              <td>{product.produkt_id}</td>
              {editingProductId === product.produkt_id ? (
                <>
                  <td><input name="nazwa" value={editedProduct.nazwa} onChange={handleChange} /></td>
                  <td><input name="parametr" value={editedProduct.parametr} onChange={handleChange} /></td>
                  <td><input name="cena_brutto" value={editedProduct.cena_brutto} onChange={handleChange} /></td>
                  <td>
                    <button className="save-btn" onClick={handleSave}>Zapisz</button>
                    <button className="cancel-btn" onClick={handleCancel}>Anuluj</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{product.nazwa}</td>
                  <td>{product.parametr}</td>
                  <td>{product.cena_brutto} zł</td>
                  <td>
                    <button className="edit-btn" onClick={() => handleEdit(product)}>Edytuj</button>
                    <button className="delete-btn" onClick={() => handleDelete(product.produkt_id)}>Usuń</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductList;
