import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

const API_URL = "http://localhost:8000/api/products"; // Dostosuj jeśli będzie inny port backendu

function ProductList() {
  const [products, setProducts] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [editedProduct, setEditedProduct] = useState({});

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get(API_URL)
      .then(res => {
        const mappedProducts = res.data.map(item => ({
          id: item.produkt_id,
          name: item.nazwa,
          description: item.parametr,
          price: item.cena_brutto,
          stock: item.id_kategoria // tymczasowo stock, możesz tu zmienić
        }));
        setProducts(mappedProducts);
      })
      .catch(err => console.error(err));
  };

  const handleEdit = (product) => {
    setEditingProductId(product.id);
    setEditedProduct(product);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({ ...editedProduct, [name]: value });
  };

  const handleSave = () => {
    axios.put(`${API_URL}/${editedProduct.id}`, {
      produkt_id: editedProduct.id,
      nazwa: editedProduct.name,
      parametr: editedProduct.description,
      cena_brutto: editedProduct.price,
      id_kategoria: editedProduct.stock
    })
    .then(() => {
      setEditingProductId(null);
      fetchProducts();
    })
    .catch(err => console.error(err));
  };

  const handleCancel = () => {
    setEditingProductId(null);
  };

  const handleDelete = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(() => fetchProducts())
      .catch(err => console.error(err));
  };

  return (
    <div className="container">
      <h2>Lista Produktów</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nazwa</th>
            <th>Opis</th>
            <th>Cena</th>
            <th>Stan</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td>
              {editingProductId === product.id ? (
                <>
                  <td><input name="name" value={editedProduct.name} onChange={handleChange} /></td>
                  <td><input name="description" value={editedProduct.description} onChange={handleChange} /></td>
                  <td><input name="price" value={editedProduct.price} onChange={handleChange} /></td>
                  <td><input name="stock" value={editedProduct.stock} onChange={handleChange} /></td>
                  <td>
                    <button className="save-button" onClick={handleSave}>Zapisz</button>
                    <button className="cancel-button" onClick={handleCancel}>Anuluj</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{product.name}</td>
                  <td>{product.description}</td>
                  <td>{product.price} zł</td>
                  <td>{product.stock}</td>
                  <td>
                    <button className="edit-button" onClick={() => handleEdit(product)}>Edytuj</button>
                    <button className="delete-button" onClick={() => handleDelete(product.id)}>Usuń</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductList;
