import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

function OrdersList() {
  const [orders, setOrders] = useState([]);
  const [newOrder, setNewOrder] = useState({
    klient_id: '',
    produkt_id: '',
    ilosc: 1
  });

  const API_URL = "http://localhost:8000/api/orders";  // <-- dostosuj nazwę endpointu backendowego jeśli inna

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    axios.get(API_URL)
      .then(res => setOrders(res.data))
      .catch(err => console.error(err));
  };

  const handleChange = (e) => {
    setNewOrder({
      ...newOrder,
      [e.target.name]: e.target.value
    });
  };

  const submitOrder = () => {
    axios.post(API_URL, newOrder)
      .then(() => {
        fetchOrders();
        setNewOrder({ klient_id: '', produkt_id: '', ilosc: 1 });
      })
      .catch(err => console.error(err));
  };

  return (
    <div className="container">
      <h2>Nowe Zamówienie</h2>
      <div className="order-form">
        <input
          type="number"
          name="klient_id"
          placeholder="ID Klienta"
          value={newOrder.klient_id}
          onChange={handleChange}
        />
        <input
          type="number"
          name="produkt_id"
          placeholder="ID Produktu"
          value={newOrder.produkt_id}
          onChange={handleChange}
        />
        <input
          type="number"
          name="ilosc"
          placeholder="Ilość"
          value={newOrder.ilosc}
          onChange={handleChange}
        />
        <button className="btn-save" onClick={submitOrder}>Złóż zamówienie</button>
      </div>

      <h2>Lista Zamówień</h2>
      <table>
        <thead>
          <tr>
            <th>ID Zamówienia</th>
            <th>Klient ID</th>
            <th>Produkt ID</th>
            <th>Ilość</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.zamowienie_id}>
              <td>{order.zamowienie_id}</td>
              <td>{order.klient_id}</td>
              <td>{order.produkt_id}</td>
              <td>{order.ilosc}</td>
              <td>{order.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OrdersList;
