import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css';

const API_URL = "http://localhost:8000/api/customers"; // Dostosuj URL backendu

function CustomersList() {
  const [customers, setCustomers] = useState([]);
  const [editingCustomerId, setEditingCustomerId] = useState(null);
  const [editedCustomer, setEditedCustomer] = useState({
    name: '',
    email: '',
    phone: ''
  });

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = () => {
    axios.get(API_URL)
      .then(res => setCustomers(res.data))
      .catch(err => console.error(err));
  };

  const handleEditClick = (customer) => {
    setEditingCustomerId(customer.customer_id);
    setEditedCustomer({
      name: customer.name,
      email: customer.email,
      phone: customer.phone
    });
  };

  const handleSaveClick = () => {
    axios.put(`${API_URL}/${editingCustomerId}`, editedCustomer)
      .then(() => {
        fetchCustomers();
        setEditingCustomerId(null);
      })
      .catch(err => console.error(err));
  };

  const handleDeleteClick = (id) => {
    if (window.confirm("Czy na pewno chcesz usunąć tego klienta?")) {
      axios.delete(`${API_URL}/${id}`)
        .then(() => fetchCustomers())
        .catch(err => console.error(err));
    }
  };

  const handleCancelClick = () => {
    setEditingCustomerId(null);
  };

  const handleChange = (e) => {
    setEditedCustomer({
      ...editedCustomer,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="container">
      <h2>Lista Klientów</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Imię i nazwisko</th>
            <th>Email</th>
            <th>Telefon</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(customer => (
            <tr key={customer.customer_id}>
              <td>{customer.customer_id}</td>
              <td>
                {editingCustomerId === customer.customer_id ? (
                  <input
                    type="text"
                    name="name"
                    value={editedCustomer.name}
                    onChange={handleChange}
                  />
                ) : customer.name}
              </td>
              <td>
                {editingCustomerId === customer.customer_id ? (
                  <input
                    type="text"
                    name="email"
                    value={editedCustomer.email}
                    onChange={handleChange}
                  />
                ) : customer.email}
              </td>
              <td>
                {editingCustomerId === customer.customer_id ? (
                  <input
                    type="text"
                    name="phone"
                    value={editedCustomer.phone}
                    onChange={handleChange}
                  />
                ) : customer.phone}
              </td>
              <td>
                {editingCustomerId === customer.customer_id ? (
                  <>
                    <button className="save-button" onClick={handleSaveClick}>Zapisz</button>
                    <button className="cancel-button" onClick={handleCancelClick}>Anuluj</button>
                  </>
                ) : (
                  <>
                    <button className="edit-button" onClick={() => handleEditClick(customer)}>Edytuj</button>
                    <button className="delete-button" onClick={() => handleDeleteClick(customer.customer_id)}>Usuń</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CustomersList;
