import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stock: ''
  });

  const API_URL = "http://localhost:8000/api/products"; // Dostosuj jeśli masz inny backend URL

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get(API_URL)
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  };

  const deleteProduct = (id) => {
    if (window.confirm("Czy na pewno chcesz usunąć produkt?")) {
      axios.delete(`${API_URL}/${id}`)
        .then(() => fetchProducts())
        .catch(err => console.error(err));
    }
  };

  const startEdit = (product) => {
    setEditingProduct(product.id);
    setFormData({
      name: product.name,
      description: product.description,
      price: product.price,
      stock: product.stock
    });
  };

  const handleInputChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const saveEdit = () => {
    axios.put(`${API_URL}/${editingProduct}`, formData)
      .then(() => {
        setEditingProduct(null);
        fetchProducts();
      })
      .catch(err => console.error(err));
  };

  return (
    <div className="product-container">
      <h2>Lista Produktów</h2>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nazwa</th>
            <th>Opis</th>
            <th>Cena</th>
            <th>Stan</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            editingProduct === product.id ? (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td data-label="Nazwa">
                  <input name="name" value={formData.name} onChange={handleInputChange} />
                </td>
                <td data-label="Opis">
                  <input name="description" value={formData.description} onChange={handleInputChange} />
                </td>
                <td data-label="Cena">
                  <input name="price" value={formData.price} onChange={handleInputChange} />
                </td>
                <td data-label="Stan">
                  <input name="stock" value={formData.stock} onChange={handleInputChange} />
                </td>
                <td>
                  <button onClick={saveEdit}>Zapisz</button>
                  <button className="delete" onClick={() => setEditingProduct(null)}>Anuluj</button>
                </td>
              </tr>
            ) : (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td data-label="Nazwa">{product.name}</td>
                <td data-label="Opis">{product.description}</td>
                <td data-label="Cena">{product.price} zł</td>
                <td data-label="Stan">{product.stock}</td>
                <td>
                  <button onClick={() => startEdit(product)}>Edytuj</button>
                  <button className="delete" onClick={() => deleteProduct(product.id)}>Usuń</button>
                </td>
              </tr>
            )
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductList;
